SELECT C5_CLIENTE CLIENTE,C5_LOJACLI LOJA,A1_CGC CNPJ,A1_NOME CLIENTE,C5_TRANSP COD_TPT,TRANSP.A4_NOME TRANSPORTADORA_PRINCIPAL,C5_REDESP COD_REDESP,REDESP.A4_NOME TRANSPORTADORA_REDESPACHO,CASE WHEN C5_REDESP ='' THEN A1_CEP ELSE REDESP.A4_CEP END CEP_ENTREGA,CASE WHEN C5_REDESP ='' THEN A1_END ELSE REDESP.A4_END END LOGRADOURO_ENTREGA,CASE WHEN C5_REDESP ='' THEN A1_BAIRRO ELSE REDESP.A4_BAIRRO END BAIRRO_ENTREGA,CASE WHEN C5_REDESP ='' THEN A1_MUN ELSE REDESP.A4_MUN END CIDADE_ENTREGA,CASE WHEN C5_REDESP ='' THEN A1_EST ELSE REDESP.A4_EST END ESTADO_ENTREGA,F2_PBRUTO PESO_BRUTO,F2_ZTPFRE COD_TIPO_VEICULO,DUT_DESCRI DESCR_TIPO_VEICULO,F2_ZTPOPE COD_TIPO_OPERACAO,GV4_DSTPOP DESCR_TIPO_OPERACAO,C5_FILIAL FILIAL,C5_NUM PEDIDO,C5_ZZOBS1 PARTICULARIDADE_EXPEDICAO_1,C5_ZZOBS2 PARTICULARIDADE_EXPEDICAO_2,B5_ONU ONU,F2_DOC NFE,SUM (C6_VALOR) VALOR_MERCADORIA,F2_ESPECI1 TIPO_DE_EMBALAGEM,C5_TPFRETE TIPO_DE_FRETE,CASE WHEN C5_TIPOCLI='X' THEN 'EXTERNO' ELSE 'INTERNO' END MERCADO,F2_HAWB REF_EXP_MEFROM SC5010INNER JOIN SA1010 ON A1_COD=C5_CLIENTE AND A1_LOJA=C5_LOJACLI AND SA1010.D_E_L_E_T_=''LEFT JOIN SA4010 TRANSP ON TRANSP.A4_COD=C5_TRANSP AND TRANSP.D_E_L_E_T_=''LEFT JOIN SA4010 REDESP ON REDESP.A4_COD=C5_REDESP AND REDESP.D_E_L_E_T_=''LEFT JOIN SF2010 ON F2_FILIAL=C5_FILIAL AND F2_DOC=C5_NOTA AND SF2010.D_E_L_E_T_=''LEFT JOIN DUT010 ON DUT_TIPVEI=F2_ZTPFRE AND DUT010.D_E_L_E_T_=''LEFT JOIN GV4010 ON GV4_CDTPOP=F2_ZTPOPE AND GV4010.D_E_L_E_T_=''INNER JOIN SC6010 ON C6_FILIAL=C5_FILIAL AND C6_NUM=C5_NUM AND SC6010.D_E_L_E_T_=''LEFT JOIN SB5010 ON B5_COD=C6_PRODUTO AND SB5010.D_E_L_E_T_=''WHERE C5_FILIAL='BA01' AND C5_NUM='046482'AND SC5010.D_E_L_E_T_=''GROUP BY C5_CLIENTE,C5_LOJACLI,A1_CGC,A1_NOME,C5_TRANSP,TRANSP.A4_NOME,C5_REDESP,REDESP.A4_NOME,A1_CEP,REDESP.A4_CEP,A1_END,REDESP.A4_END,A1_BAIRRO,REDESP.A4_BAIRRO,A1_MUN,REDESP.A4_MUN,A1_EST,REDESP.A4_EST,F2_PBRUTO,F2_ZTPFRE,DUT_DESCRI,F2_ZTPOPE,GV4_DSTPOP,C5_FILIAL,C5_NUM,C5_ZZOBS1,C5_ZZOBS2,F2_DOC,F2_ESPECI1,C5_TPFRETE,B5_ONU,C5_TIPOCLI,F2_HAWB